import React, { useReducer } from "react";
import axios from "axios";
import UserContext from "./userContext";
import UserReducer from "./userReducer";

const UserState = (props) => {
  const initialState = {
    user: {
      email: "",
      password: "",
      firstname: "",
      lastname: "",
      error: "",
      success: false,
    },
  };
  const [state, dispatch] = useReducer(UserReducer, initialState);

  const userLogin = async ({ email, password }) => {
    const request = await axios.post("api/login", { email, password });

    /*
      outpot:{
        isAuth:true,
        id:"{...}",
        email:"{...email}"
      }
      */

    // dispatch({
    //   type: "USER_LOGIN",
    //   payload: request,
    // });
  };
  const authCheck = () => {
    const request = axios.get("/api/auth").then((response) => response.data);

    dispatch({
      type: "USER_AUTH",
      payload: request,
    });
  };
  const getUser = () => {
    const request = axios.get("api/users").then((res) => res.data);
    dispatch({
      type: "GET_USERS",
      playload: request,
    });
  };
  const userRegister = async ({ password, firstname, lastname, email }) => {
    const request = await axios.post("/api/register", {
      email,
      password,
      lastname,
      firstname,
    });

    dispatch({
      type: "USER_REGISTER",
      payload: request.data,
    });
  };

  return (
    <div>
      <UserContext.Provider
        value={{
          user: state.user,
          userLogin,
          authCheck,
          userRegister,
          getUser,
        }}>
        {props.children}
      </UserContext.Provider>
    </div>
  );
};

export default UserState;
