import React, { useReducer } from "react";
import axios from "axios";
import FoodContext from "./foodContext";
import FoodReducer from "./foodReducer";

const FoodState = (props) => {
  const initialState = {
    name: "",
    macro: [],
  };
  const [state, dispatch] = useReducer(FoodReducer, initialState);
  //search food
  const searchfood = async (text) => {
    //for now text is "rice";
    const res = await axios.get(
      `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=IckhwEjFyp6BaAC0oonKEpKjTbBjgG8b5EqBggnm&query=${text}`
    );
    //function to get only the needed items from the foodnutrients
    let result = res.data.foods[0].foodNutrients.filter(
      (m) =>
        m.nutrientId === 1003 ||
        m.nutrientId === 1004 ||
        m.nutrientId === 1005 ||
        m.nutrientId === 1008
    );
    let tmp = {
      name: text,
      macro: {name: text,
         Protien: result.filter((m) => m.nutrientId === 1003)[0].value, 
         Fat: result.filter((m) => m.nutrientId === 1004)[0].value,
         Carbohydrate: result.filter((m) => m.nutrientId === 1005)[0].value,
         Calories: result.filter((m) => m.nutrientId === 1008)[0].value,},
    };
    console.log(tmp);
    dispatch({
      type: "SEARCH_FOOD",
      payload: [result, text],
    });
  };

  return (
    <div>
      <FoodContext.Provider
        value={{
          name: state.name,
          macro: state.macro,
          searchfood,
        }}>
        {props.children}
      </FoodContext.Provider>
    </div>
  );
};

export default FoodState;
