import React, { useContext, useState } from "react";
import PlateItems from "./plateItems";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  const [disply,setDisply]=useState(false)
  const addFood = () => {
      disply:true
  };

  return (
    <div>
      {macro.length ? <button onClick={addFood}>add to plate</button> : null}
      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein</th>
            <th>Fat</th>
            <th>Carbs</th>
            <th>Calories</th>
          </tr>
        </thead>
        <tbody>
          {macro.map((macro) => (
            <PlateItems key={macro.nutrientId} macro={macro} />
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Plate;
