import React, { useContext, useState } from "react";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  const [plate, setPlate] = useState([]);
  console.log(plate);

  return (
    <div>
      {macro.length ? (
        <button onClick={() => addFood(plate.push(macro))}>
          add to plate
        </button>
      ) : null}
      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein</th>
            <th>Fat</th>
            <th>Carbs</th>
            <th>Calories</th>
          </tr>
        </thead>
      </Table>
    </div>
  );
};

export default Plate;
