export default (state, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        user: action.payload,
      };
    case "USER_AUTH":
      return {
        ...state,
        user: action.payload.user,
      };
    case "GET_USER":
      return {
        ...state,
      };
    case "USER_REGISTER":
      let tmp=state.slice();
      tmp.succuess=action.payload.succuess;
      tmp.firstname=action.payload.user.firstname;
      tmp.lastname=action.payload.succuess;
      tmp.email=action.payload.succuess;
      tmp.firstname=action.payload.user.firstname;


    default:
      return state;
  }
};
