import React, { useState } from "react";
import { Button } from "react-bootstrap";
import FormLogin from "./formLogin";

const Register = () => {
  const [showRegister, setShowRegister] = useState("none");
  const [showLogin, setShowLogin] = useState("none");
  //toggle display of login form
  const toggleRegister = () => {
    if (showLogin === "none") {
      if (showRegister === "none") {
        setShowRegister("block");
      } else if (showRegister === "block") {
        setShowRegister("none");
      }
    }
  };
  const toggleLogin = () => {
    if (showRegister === "none") {
      if (showLogin === "none") {
        setShowLogin("block");
      } else if (showLogin === "block") {
        setShowLogin("none");
      }
    }
  };
  return (
    <div>
      <Button variant="info" style={{ margin: "5%" }} onClick={toggleRegister}>
        Sign In
      </Button>
      <Button variant="secondary" onClick={toggleLogin}>
        Login
      </Button>
      <div className="div-popup" style={{ display: showRegister }}>
        <form className="form-popup">
          <input
            className="namebtn"
            type="text"
            placeholder="First Name"
            name="first"
            required
          />
          <br />
          <input
            className="namebtn"
            type="text"
            placeholder="Last Name"
            name="last"
            required
          />
          <br />
          <input
            className="emailbtn"
            type="text"
            placeholder="Email"
            name="email"
            required
          />
          <br />
          <input
            className="passbtn"
            type="password"
            placeholder="Password"
            name="psw"
            required
          />
          <br />
          <input
            className="passbtn"
            type="password"
            placeholder="Confirm Password"
            name="psw"
            required
          />
          <br />
          <Button variant="success" type="submit">
            Register
          </Button>
        </form>
      </div>
      <div className="div-popup" style={{ display: showLogin }}>
      FormLogin
      </div>
    </div>
  );
};

export default Register;
