import React, { useContext } from "react";
import { Button } from "react-bootstrap";
import { useForm } from "./useForm";
import UserContext from "../../context/User/userContext";

const FormRegister = () => {
  const userContext = useContext(UserContext);
  const { user } = userContext;
  const [values, handelChange] = useForm({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    
  });
  const checkRegister = (e) => {
    e.preventDefault();

    userContext.userRegister(values);
  };

  return (
    <div>
      <form className="form-popup" onSubmit={checkRegister}>
        <input
          className="namebtn"
          type="text"
          placeholder="First Name"
          name="firstname"
          required
          value={values.first}
          onChange={handelChange}
        />
        <br />
        <input
          className="namebtn"
          type="text"
          placeholder="Last Name"
          name="lastname"
          required
          value={values.last}
          onChange={handelChange}
        />
        <br />
        <input
          className="emailbtn"
          type="text"
          placeholder="Email"
          name="email"
          required
          value={values.email}
          onChange={handelChange}
        />
        <br />
        <input
          className="passbtn"
          type="password"
          placeholder="Password"
          name="password"
          required
          value={values.password}
          onChange={handelChange}
        />
        <br />
        <input
          className="passbtn"
          type="password"
          placeholder="Confirm Password"
          name="confirmP"
          required
          value={values.confirmP}
          onChange={handelChange}
        />
        <br />
        <Button variant="success" type="submit">
          Register
        </Button>
        <div>error msg</div>
      </form>
    </div>
  );
};

export default FormRegister;
