import React, { useReducer } from "react";
import axios from "axios";
import FoodContext from "./foodContext";
import FoodReducer from "./foodReducer";

const FoodState = (props) => {
  const initialState = {
    name: "",
    macro: { },
  };
  const [state, dispatch] = useReducer(FoodReducer, initialState);
  //search food
  const searchfood = async (text) => {
    //for now text is "rice";
    const res = await axios.get(
      `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=IckhwEjFyp6BaAC0oonKEpKjTbBjgG8b5EqBggnm&query=${text}`
    );
    let macro = { protein: 0 };
    const output = res.data.foods[0].foodNutrients.map((e) => {
      if (e.nutrientId === 1003) {
        let protein = e.value;
      } else if (e.nutrientId === 1004) {
        let fat = e.value;
      } else if (e.nutrientId === 1005) {
        let carb = e.value;
      }
    });
    console.log(res.data.foods[0].foodNutrients);
    dispatch({
      type: "SEARCH_FOOD",
      payload: res.data.foods[0],
    });
  };

  return (
    <div>
      <FoodContext.Provider
        value={{
          name: state.name,
          macro: state.protien,
          carbs: state.carbs,
          fat: state.fat,
          searchfood,
        }}>
        {props.children}
      </FoodContext.Provider>
    </div>
  );
};

export default FoodState;
