import React, { useReducer } from "react";
import axios from "axios";
import UserContext from "./userContext";
import UserReducer from "./userReducer";

const UserState = (props) => {
  const initialState = {
    user: {
      email: "",
      password: "",
      firstname: "",
      lastname: "",
      error: "",
      success: false,
    },
  };
  const [state, dispatch] = useReducer(UserReducer, initialState);

  const userRegister = ({ password, firstname, lastname, email }) => {
    const request = axios
      .post("/api/register", {
        email:'test@gmail.com',
        password,
        lastname,
        firstname,
      })
      .then((response) => console.log(response.data))
      .catch((error) => console.log(error, "cant see"))
      .finally(console.log("userRegister promise"));

    console.log({
      email,
      password,
      lastname,
      firstname,
    });

    /*
    {
      "success": true,
      "user": {
        "role": 0,
        "_id": "******",
        "email": "*******",
        "password": "hashedPassword",
        "firstname": "*****",
        "lastname": "******",
        "__v": 0
      }
    }
      */
    dispatch({
      type: "USER_REGISTER",
      payload: request.data,
    });
  };
  const userLogin = ({ email, password }) => {
    const request = axios
      .post("api/login", { email, password })
      .then((response) => console.log(response.json.data))
      .catch((error) => console.log(error))
      .finally(console.log("useLogin promise"));

    /*
      outpot:{
        isAuth:true,
        id:"{...}",
        email:"{...email}"
      }
      */

    dispatch({
      type: "USER_LOGIN",
      payload: request,
    });
  };
  const authCheck = () => {
    const request = axios.get("/api/auth").then((response) => response.data);

    dispatch({
      type: "USER_AUTH",
      payload: request,
    });
  };
  const getUser = () => {
    const request = axios.get("api/users").then((res) => res.data);
    dispatch({
      type: "GET_USERS",
      playload: request,
    });
  };

  return (
    <div>
      <UserContext.Provider
        value={{
          user: state.user,
          userLogin,
          authCheck,
          userRegister,
          getUser,
        }}>
        {props.children}
      </UserContext.Provider>
    </div>
  );
};

export default UserState;
