import React, { useContext } from "react";
import FoodContext from "../../context/foodContext";
const Food = () => {
  const foodContext = useContext(FoodContext);
  const { macro } = foodContext;
  return (
    <div>
      {/* here will be a table or 2X3 */}
      {macro.map(e=>{
        if(e.nutrientId===1003){
          return (<h3>Protein: {e.value}</h3>)
        }
        else if(e.nutrientId===1004){
          return (<h3>Fat: {e.value}</h3>)
        }
      })}
      <h3>after that</h3>
    </div>
  );
};

export default Food;
