import React from "react";
import { Button } from "react-bootstrap";

const register = () => {
  const [showLogin, setShowLogin] = useState("none");
  //toggle display of login form
  const toggle=()=>{
      if (showLogin==="none"){
        setShowLogin
      }
  }
  return (
    <div>
      <Button variant="info" style={{ margin: "5%" }}>
        Sign In
      </Button>
      <Button variant="secondary" onClick={toggle()}>Login</Button>
      <div className="div-popup">
        <form className="form-popup">
          <input
            className="emailbtn"
            type="text"
            placeholder="Email"
            name="email"
            required
          />
          <br />
          <input
            className="passbtn"
            type="password"
            placeholder="Password"
            name="psw"
            required
          />
          <br />
          <Button variant="success" type="submit">
            Login
          </Button>
        </form>
      </div>
    </div>
  );
};

export default register;
