export default (state, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        user: action.payload,
      };
    case "USER_AUTH":
      return {
        ...state,
        user: action.payload.user,
      };
    case "GET_USER":
      return {
        ...state,
      };
    case "USER_REGISTER":
      let tmp = {...state};      
      tmp.succuess = {...action.payload.succuess};
      tmp.firstname = action.payload.user.firstname;
      tmp.lastname = action.payload.user.lastname;
      tmp.email = action.payload.user.email;
      tmp.password = action.payload.user.password;
      return tmp;

    default:
      return state;
  }
};
