import React, { useContext, useState } from "react";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro } = foodContext;
  const [disply, setDisply] = useState(false);
  const [plate, setPlate] = useState([]);
  console.log(plate);

  const deleteFood = (i) => {
    setPlate(plate.splice(i, 1));
  };

  return (
    <div>
      {macro.name ? (
        <button
          onClick={() => {
            setPlate([...plate, macro]);
            setDisply(true);
          }}>
          add to plate
        </button>
      ) : null}

      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein (100g)</th>
            <th>Fat (100g)</th>
            <th>Carbs (100g)</th>
            <th>Calories (100g)</th>
            <th></th>
          </tr>
        </thead>
        {disply ? (
          <tbody>
            {plate.map((e) => (
              <tr key={Math.floor(Math.random() * 100)}>
                <th>{e.name}</th>
                <th>{e.Protien}</th>
                <th>{e.Fat}</th>
                <th>{e.Carbohydrate}</th>
                <th>{e.Calories}</th>
                <th>
                  <button onClonClick={()=>
                  setPlate(plate.splice)
                  }>X</button>
                </th>
              </tr>
            ))}
            <tr>
              <th>total</th>
              <th>{plate.reduce((acc, curr) => acc + curr.Protien, 0)}</th>
              <th>{plate.reduce((acc, curr) => acc + curr.Fat, 0)}</th>
              <th>{plate.reduce((acc, curr) => acc + curr.Carbohydrate, 0)}</th>
              <th>{plate.reduce((acc, curr) => acc + curr.Calories, 0)}</th>
            </tr>
          </tbody>
        ) : null}
      </Table>
    </div>
  );
};

export default Plate;
