import React, { useContext } from "react";
import FoodContext from "../../context/foodContext";

const Food = () => {
  const foodContext = useContext(FoodContext);
  const { macro } = foodContext;
  console.log(macro);
  //for each array element we look at his ingeredientId
  //if its match 1003 we return a div with its value
  return (
    <div>
      {
        macro.forEach(e=>{
          if(e.nutrientId===1003){
            return (
              <div></div>
            )

          }
        })
      }
      <h3>after that</h3>
    </div>
  );
};

export default Food;
