export default (state, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      return {
        ...state,
        user: action.payload,
      };
    case "USER_AUTH":
      return {
        ...state,
        user: action.payload.user,
      };
    case "GET_USER":
      return {
        ...state,
      };
    case "USER_REGISTER":
      return {
        ...state,
        user: action.payload,
      };

    default:
      return state;
  }
};
