import React, { useContext, useState } from "react";
import PlateItems from "./plateItems";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  const [disply, setDisply] = useState(false);

  console.log(`name is : ${name}`);
  console.log(`macro is ${macro}`);
  return (
    <div>
      {macro.length ? (
        <button
          onClick={() => {
            setDisply(true);
          }}>
          add to plate
        </button>
      ) : null}
      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein</th>
            <th>Fat</th>
            <th>Carbs</th>
            <th>Calories</th>
          </tr>
        </thead>
        {disply ? (
          <tbody>
            {/* {macro.map((macro) => (
              <PlateItems key={macro.nutrientId} macro={macro} name={name} />
            ))} */}
            <tr>
              {macro.map((macro) => {
                macro.nutrientName === 1003 ? (
                  <th>{e.value}</th>
                ) : macro.nutrientName === 1004 ? (
                  <th>{e.value}</th>
                ) : macro.nutrientName === 1005 ? (
                  <th>{e.value}</th>
                ) : e.nutrientName === 1008 ? (
                  <th>{e.value}</th>
                ) : null;
              })}
            </tr>
          </tbody>
        ) : null}
      </Table>
    </div>
  );
};

export default Plate;
