import React, { useContext } from "react";
import FoodContext from "../../context/foodContext";
import Fooditems from "./fooditems";

const Food = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  console.log(macro);
  //for each element we look at his ingeredientId
  //if its match 1003 we return a div with its value

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>{name}</h1>
      {macro.map((macro) => (
        <Fooditems key={macro.nutrientId} macro={macro} />
      ))}
      {macro.length ? <button style={{  transform: "translateY(-50%)" }}>add to plate</button> : null}
    </div>
  );
};

export default Food;
