    <div>
      <form className="form-popup" onSubmit={checkLogin}>
        <input
          className="emailbtn"
          type="text"
          placeholder="Email"
          name="email"
          required
          value={values.email}
          onChange={handelChange}
        />
        <br />
        <input
          className="passbtn"
          type="password"
          placeholder="Password"
          name="password"
          required
          value={values.password}
          onChange={handelChange}
        />
        <br />
        <Button variant="success" type="submit">
          Login
        </Button>
      </form>
    </div>
  );
};

export default FormLogin;
