import React, { useContext, useState } from "react";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro } = foodContext;
  const [disply, setDisply] = useState(false);
  const [plate, setPlate] = useState([]);
  console.log(plate);

  return (
    <div>
      {macro.name ? (
        <button
          onClick={() => {
            setPlate([...plate, macro]);
            setDisply(true);
          }}>
          add to plate
        </button>
      ) : null}

      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein (100g)</th>
            <th>Fat (100g)</th>
            <th>Carbs (100g)</th>
            <th>Calories (100g)</th>
          </tr>
        </thead>
        {disply ? <tbody>
          {plate.map(e=>
                <tr>
                  <th>{e.name}</th>
                  <th>{e.Protein}</th>
                  <th>{e.name}</th>
                  <th>{e.name}</th>
                </tr>
              )}
        </tbody> : null}
      </Table>
    </div>
  );
};

export default Plate;
