import React, { useContext } from "react";
import FoodContext from "../../context/foodContext";
import Fooditems from "./fooditems";
import { Container, Row, Col } from "react-bootstrap";

const Food = () => {
  const foodContext = useContext(FoodContext);
  const { macro } = foodContext;
  console.log(macro);
  return (
    <div>
      <h1 style={{ textAlign: "center" }}>{macro.name}</h1>
      <Container>
        <Row>
          <Col>
            {macro.Protien}
          </Col>

          <Col>
          {macro.Protien}
          </Col>

          <Col>
            {nutrientName}: {value} {unitName}
          </Col>

          <Col>
            Total Calories: {value} {unitName}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Food;
