import React, { useContext } from "react";
import FoodContext from "../../context/foodContext";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;

  const onClick = () => {
    console.log(name);
    console.log(macro);
  };
  return (
    <div>
      {macro.length ? <button onClick={onClick}>add to plate</button> : null}
      <h2>my plate:</h2>
      <table>
          <tr>
              <th>Name</th>
              <th>Protien</th>
          </tr>
      </table>
    </div>
  );
};

export default Plate;
