import React, { useContext, useState } from "react";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  const [disply, setDisply] = useState(false);
  const [plate, setPlate] = useState([]);
  const [itemName, setItemName] = useState("");

  return (
    <div>
      {macro.length ? (
        <button
          onClick={() => {
            setDisply(true);
            setItemName()
            setPlate([...plate, ...macro, name]);
          }}>
          add to plate
        </button>
      ) : null}
      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein (100g)</th>
            <th>Fat (100g)</th>
            <th>Carbs (100g)</th>
            <th>Calories (100g)</th>
          </tr>
        </thead>
        {disply ? (
          <tbody>
            <tr>
              <th>{plate[plate.length - 1]}</th>
              <th>{plate.filter((m) => m.nutrientId === 1003)[0].value}</th>
              <th>{plate.filter((m) => m.nutrientId === 1004)[0].value}</th>
              <th>{plate.filter((m) => m.nutrientId === 1005)[0].value}</th>
              <th>{plate.filter((m) => m.nutrientId === 1008)[0].value}</th>
            </tr>
          </tbody>
        ) : null}
      </Table>
    </div>
  );
};

export default Plate;
