import React, { useContext, useState } from "react";
import FoodContext from "../../context/foodContext";
import { Table } from "react-bootstrap";

const Plate = () => {
  const foodContext = useContext(FoodContext);
  const { macro, name } = foodContext;
  const [disply, setDisply] = useState(false);
  const [plate,setPlate]=useState([]);

  return (
    <div>
      {macro.length ? (
        <button
          onClick={() => {
            setDisply(true);
            setPlate(...macrp)
          }}>
          add to plate
        </button>
      ) : null}
      <h2>my plate:</h2>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Protein</th>
            <th>Fat</th>
            <th>Carbs</th>
            <th>Calories</th>
          </tr>
        </thead>
        {disply ? (
          <tbody>
            <tr>
              <th>{name}</th>
              <th>{macro.filter((m) => m.nutrientId === 1003)[0].value}</th>
              <th>{macro.filter((m) => m.nutrientId === 1004)[0].value}</th>
              <th>{macro.filter((m) => m.nutrientId === 1005)[0].value}</th>
              <th>{macro.filter((m) => m.nutrientId === 1008)[0].value}</th>
            </tr>
          </tbody>
        ) : null}
      </Table>
    </div>
  );
};

export default Plate;
